li.NOResult:

if me:GetGLevel() >= 10 then
	me:AddMsg(XT("[NAME]以强横的修为击溃了血肉傀儡，但似乎机缘差了些：战斗的余波竟然波及到了传承本身，传承和傀儡一同毁灭，[NAME]暗叹一声，只得离开。"));me:AddSchoolScore(1,100);story:FinishSecret();
else				
	me:AddMsg(XT("[NAME]试图击溃血肉傀儡，但是却太过不自量力，被傀儡反击身负重伤，沦为修行界的笑柄。"));me:AddSchoolScore(1,-300);me:AddDamgeFromCache(story.DamageCache,4);story:FinishSecret();
end;

li.OKResult:

if me:GetMatchingWithGong("Body_Gong_TunTian") >= 1 then
	me:AddMsg(XT("[NAME]上前接受试炼，竟然非常适合这门道统，顺利接受了道统的传承。"));me:DropAwardItem("Item_Dan_TreeEXP", 1);me:UnLockGong("Body_Gong_TunTian");story:FinishSecret();   
else
	me:AddMsg(XT("[NAME]上前接受试炼，但可惜最终并没有通过检测，只能无奈离去"));
end
